import React, { useState, useLayoutEffect } from 'react';
import { StatusBar } from 'expo-status-bar';
import { KeyboardAvoidingView, StyleSheet, View } from 'react-native';
import { Button, Input, Text, Image } from 'react-native-elements';
import axios from 'axios';
const AddChatProyect = ({ navigation }) => {
    const [proyect, setProyect] = useState({
        deliver: '',
        address: '',
        contact: '',
    });
    const { deliver, address, contact } = proyect
    const newProyect = async () => {
        try {
            let res = await axios({
                method: "post",
                url: "http://192.168.0.28:5000/api/user/native/",
                data: { deliver: deliver, address: address, contact: contact },
                config: { headers: { "Content-Type": "application/json" } }
            })
            deviceStorage.saveKey("x-token", res.data.token);
            newJWT(res.data.token);
        } catch (error) {
            console.log('New Proyecy Error: ' + error.message);
        }
    };
    useLayoutEffect(() => {
        navigation.setOptions({
            headerTitle: () => <Image
                source={{ uri: 'https://i.ibb.co/zHMVH1h/logo.png' }}
                style={{
                    width: 300, height: 50, display: 'flex', margin: 'auto'
                }}
            />,
            headerStyle: {
                backgroundColor: '#fff',
            },
            headerTintColor: 'black'
        })
    }, [navigation])
    return (
        <KeyboardAvoidingView style={styles.container}>
            <StatusBar style='light' />
            <Text h3 style={{ marginBottom: 15 }}>New proyect</Text>
            <View style={styles.inpurContainer}>
                <Input style={{ paddingLeft: 5 }} placeholder='Deliver to' autoFocus type='text' value={deliver} onChangeText={deliver => setUser({ ...user, deliver })} />
                <Input style={{ paddingLeft: 5 }} placeholder='Job site address' autoFocus type='text' value={address} onChangeText={address => setUser({ ...user, address })} />
                <Input style={{ paddingLeft: 5 }} placeholder='Job contact' autoFocus type='text' value={contact} onChangeText={contact => setUser({ ...user, contact })} />
            </View>
            <Button raised containerStyle={styles.button} title='Next'></Button>
        </KeyboardAvoidingView>
    )
}

export default AddChatProyect

const styles = StyleSheet.create({
    container: {
        padding: 10,
        flex: 1,
        textAlign: 'center',
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "white",
    },
    inpurContainer: {
        width: 300,
        // marginBottom: -10
    },
    button: {
        width: 200,
        marginTop: 3,
    }
})
