import React, { useState, useEffect } from 'react';
import { StatusBar } from 'expo-status-bar';
import { KeyboardAvoidingView, StyleSheet, View } from 'react-native';
import { Button, Input, Icon } from 'react-native-elements'
import { auth } from '../Firebase';
const LoginScreen = ({ navigation }) => {
    const [email, setEmail] = useState('');
    const [pass, setPass] = useState('');
    const signIn = () => {
        // auth.createUserWithEmailAndPassword(email, password)
        //     .then(authUser => {
        //         authUser.user.updateUser({
        //             displayName: name,
        //             //photo
        //         })
        //     })
        //     .catch(error => alert(error.message))
    };
    useEffect(() => {
        const unsuscribe = auth.onAuthStateChanged(authUser => {
            if (authUser) return navigation.replace('HOME');
        })
        return unsuscribe;
    }, []);
    return (
        <KeyboardAvoidingView enabled style={styles.constainerFather}>
            <StatusBar style='light' />
            <Icon raised
                name='user-circle'
                size={50}
                type='font-awesome'
                color='#2c6bed'
                style={{ height: 100 }}
            />
            <View style={styles.inpurContainer}>
                <Input placeholder='Email' autoFocus type='email' value={email} onChangeText={(text) => setEmail(text)} />
                <Input placeholder='Password' secureTextEntry type='password' value={pass} onChangeText={(text) => setPass(text)} />
            </View>
            <Button containerStyle={styles.button} title='Login' onPress={signIn}></Button>
            <Button onPress={() => navigation.navigate('REGISTER')} containerStyle={styles.button} title='Register' type='outline'></Button>
            <View style={{ height: 10 }}>
            </View>
        </KeyboardAvoidingView>
    )
}
export default LoginScreen
const styles = StyleSheet.create({
    constainerFather: {
        padding: 10,
        flex: 1,
        textAlign: 'center',
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "white",
    },
    inpurContainer: {
        width: 300,
        marginBottom: -10
    },
    button: {
        width: 200,
        marginTop: 10,
    }
})
