import React, { useEffect, useState, useLayoutEffect } from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import { ListItem, Avatar, Icon, Image } from 'react-native-elements';
import { Table, Button, Modal, ModalBody, FormGroup, ModalFooter, ModalHeader } from 'reactstrap';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
const ViewProyectScreen = ({ navigation, route }) => {
    const [item, setItem] = useState(null);
    const [id, setId] = useState(null);
    const [bom, setBom] = useState([]);

    const newProyect = async () => {
        try {
            let res = await axios({
                method: "get",
                url: `http://localhost:8001/api/dc/all/${id}`,
            });
            if (res) {
                setBom(res.data);
                // let a = [];
                // for (let valor of res.data) {
                //     if (valor.match(/'_o'/)) {
                //         a.push(valor);
                //         console.log(valor, typeof valor, valor.length);
                //     }
                // }
                // console.log(a);
            }
            // 
        } catch (error) {
            console.log('New Proyecy Error: ' + error.message);
        }
    };
    useLayoutEffect(() => {
        navigation.setOptions({
            headerTitle: () => <Image
                source={{ uri: 'https://i.ibb.co/zHMVH1h/logo.png' }}
                style={{
                    width: 300, height: 50, display: 'flex', margin: 'auto'
                }}
            />,
            headerStyle: {
                backgroundColor: '#fff',
                justifyContent: 'space-between'
            },
            headerTitleColor: 'black',
        });
    }, [navigation]);
    useEffect(() => {
        if (bom) {
            console.log(bom);
            for (let d in bom) {
                let part = '_part';
                let Part = [];
                if (d.match(part)) {
                    Part.push({ d: bom[d] })
                    console.log(d); // It gives you property name
                    console.log(bom[d]); // And this gives you its value
                }
                console.log(Part);
            }

            //            let algo = bom.split(/'_o'/);
            // console.log(algo);
        }
    }, [bom])
    useEffect(() => {
        if (id) newProyect();
    }, [id])
    useEffect(() => {
        setItem(route.params.item);
        setId(route.params.id);
    }, [])
    return (
        <>
            <ListItem bottomDivider>
                {/* <Avatar rounded source={{
                uri: "https://i.ibb.co/52bXNrJ/IMG-20200928-WA0005-jpeg.jpg",
            }} /> */}
                <ListItem.Content>
                    <ListItem.Title style={{ fontWeight: '800' }}>
                        {item?.deliver}
                    </ListItem.Title>
                    <ListItem.Subtitle numberOfLines={1} ellipsizeMode='tail'>
                        {item?.address}
                    </ListItem.Subtitle>
                    <ListItem.Subtitle numberOfLines={1} ellipsizeMode='tail'>
                        {item?.contact}
                    </ListItem.Subtitle>
                </ListItem.Content>
            </ListItem>
            <Table>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>PART #</th>
                        <th>ORDER</th>
                    </tr>
                </thead>
                <tbody>
                    {}
                </tbody>
            </Table>
        </>
    )
}

export default ViewProyectScreen

const styles = StyleSheet.create({})
