import React, { useLayoutEffect } from 'react'
import { SafeAreaView, ScrollView, StyleSheet, View, TouchableOpacity } from 'react-native';
import { AntDesign, SimpleLineIcons } from '@expo/vector-icons'
import { Avatar, Icon } from 'react-native-elements';
import { auth } from '../Firebase';
import CustomLinkList from '../componets/CustomListItem';
const HomeScreen = ({ navigation }) => {
    const signOutUser = () => {
        auth.signOut().then(() => {
            navigation.replace('SHOREBREAK ENERGY');
        })
    }
    useLayoutEffect(() => {
        navigation.setOptions({
            title: 'SHOREBREAK ENERGY',
            headerStyle: { backgroundColor: '#fff' },
            headerTitleStyle: { color: 'black', textAlign: 'center', marginRight: 50 },
            headerTitleColor: 'black',
            headerLeft: () => (
                <View style={{}}>
                    <TouchableOpacity onPress={signOutUser} activeOpacity={0.5}>
                        {auth?.currentUser?.photoURL ?
                            <Avatar rounded source={{ uri: auth?.currentUser?.photoURL }} /> :
                            <Icon raised
                                name='user-circle'
                                //size={50}
                                type='font-awesome'
                                color='#2c6bed'
                            //style={{ height: 100 }}
                            />}
                    </TouchableOpacity>
                </View>
            ),
            headerRight: () => (
                <View style={{
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                    width: 80,
                    marginRight: 20
                }}>
                    {/* <TouchableOpacity activeOpacity={0.5}>
                        <AntDesign name='camerao' size={24} color='black' />
                    </TouchableOpacity> */}
                    <TouchableOpacity onPress={() => navigation.navigate('NEW')} activeOpacity={0.5}>
                        <SimpleLineIcons name='pencil' size={24} color='black' />
                    </TouchableOpacity>
                </View>
            )
        });
    }, [navigation]);
    return (
        <SafeAreaView>
            <ScrollView>
                <CustomLinkList />
            </ScrollView>
        </SafeAreaView>
    )
}

export default HomeScreen

const styles = StyleSheet.create({})
